import React, {useRef, useEffect} from "react";
import "./calculator.css";

import { BTN_ACTIONS, btns } from "./btnConfig";

const Calculator = () => {
  const btnsRef = useRef(null)
  const expRef = useRef(null)

  useEffect(()=>{
    const btns = Array.from(btnsRef.current.querrySelectorAll('button'));

    btns.forEach(e=>e.style.height = e.offser)
  },[])
  return (
    <div className="calculator">
      <div className="calculator_result">
        <div ref={expRef} className="calculator_result_exp">99999</div>
        <div className="calculator_result_exp"></div>
      </div>
      <div ref={btnsRef} className="calculator_btns">
        {btns.map((item, index) => (
          <button key={index} className={item.class}>
            {item.display}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Calculator;
