import { createSlice } from "@reduxjs/toolkit";

export const rootReducer = createSlice({
  name: "calc",
  initialState: {
    result: 0,
    display: [],
    ans: [],
  },
  reducers: {
    //action
    ADD_NUMBERS: (state, action) => {
      state.display.push(action.payload);
      //   console.log(state);
    },
    BUTTON_EQUAL: (state) => {
      roresult
    },
  },
});

export const { ADD_NUMBERS, BUTTON_EQUAL } = rootReducer.actions;
